	Merge Projects by Opportunity Ids
**************************************************************************/
-- check to see if there is a pmt_merge_project_temp function, if so drop it
DROP FUNCTION IF EXISTS pmt_merge_project_temp(character varying, character varying, character varying, character varying, date, date, integer, integer, numeric(100,2)) CASCADE;
-- create a temporary project merging function to support logic
CREATE OR REPLACE FUNCTION pmt_merge_project_temp(oppid character varying, t character varying, l character varying, d character varying, s date, e date, p integer, fte integer, g numeric(100,2)) RETURNS BOOLEAN AS $$
DECLARE 
  new_project_id integer;
  new_financial_id integer;
  new_participation_id integer;
  c_id integer;
  project_record record;  
  financial_record record;
  participation_record record;    
  participation_taxonomy_record record;
  project_taxonomy_record record;
  function_stamp character varying(50);
BEGIN
  function_stamp := 'BMGF Data Scrub';	

  -- create new project record (717)	
  EXECUTE 'INSERT INTO project(title, label, description, start_date, end_date, people_affected, fte, opportunity_id, created_by, created_date, updated_by, updated_date) VALUES (' || 
	coalesce(quote_literal($2),'NULL') || ',' || coalesce(quote_literal($3),'NULL') || ',' || coalesce(quote_literal($4),'NULL') || ',' || quote_literal($5) || ',' || quote_literal($6) || 
	',' || coalesce(cast($7 as text), 'null') || ',' || coalesce(cast($8 as text), 'null') || ',' || quote_literal($1) || ',' || quote_literal(function_stamp) || ',' || quote_literal(current_date) || ',' || quote_literal(function_stamp) || 
	',' || quote_literal(current_date) || ') RETURNING project_id;' INTO new_project_id;
  
  -- -- create new financial record with single value for new project id
  IF $9 IS NOT NULL THEN
    EXECUTE 'INSERT INTO financial(project_id, amount, start_date, end_date, created_by, created_date, updated_by, updated_date) VALUES (' ||
	new_project_id || ',' ||  $9 || ',' || quote_literal($5)  || ',' || quote_literal($6)  || ',' || quote_literal(function_stamp) || ',' ||  quote_literal(current_date) || ',' || quote_literal(function_stamp) ||
	',' || quote_literal(current_date) || ') RETURNING financial_id;' INTO new_financial_id;
  END IF;   
  
  -- loop through all associated projects
  FOR project_record IN SELECT * FROM project WHERE opportunity_id = $1 AND created_by <> function_stamp
  LOOP
    -- mark project record retired/inactive    
    EXECUTE 'UPDATE project SET active = false, retired_by = ' || new_project_id || ', updated_by=' || quote_literal(function_stamp) || ', updated_date=' || quote_literal(current_date) || ' WHERE project_id = ' || project_record.project_id;
    -- update associated activity records with new project id
    EXECUTE 'UPDATE activity SET project_id = ' || new_project_id || ', updated_by=' || quote_literal(function_stamp) || ', updated_date=' || quote_literal(current_date) || ' WHERE project_id = ' || project_record.project_id;
    -- update associated location records with new project id
    EXECUTE 'UPDATE location SET project_id = ' || new_project_id || ', updated_by=' || quote_literal(function_stamp) || ', updated_date=' || quote_literal(current_date) || ' WHERE project_id = ' || project_record.project_id;
    -- replace project_id with new id in detail table
    EXECUTE 'UPDATE detail SET project_id = ' || new_project_id || ', updated_by=' || quote_literal(function_stamp) || ', updated_date=' || quote_literal(current_date) || ' WHERE project_id = ' || project_record.project_id;    
    -- inactivate financial record setting retired by value of financial record
    FOR financial_record IN SELECT * FROM financial WHERE project_id = project_record.project_id AND activity_id IS NULL
    LOOP
      EXECUTE 'UPDATE financial SET active = false, retired_by = ' || new_financial_id || ', updated_by=' || quote_literal(function_stamp) || ', updated_date=' || quote_literal(current_date) || ' WHERE financial_id = ' || financial_record.financial_id;
    END LOOP;               
  END LOOP;

  -- inactivate/retire old project participation records marking the new values
  FOR participation_record IN SELECT pp.* FROM participation pp JOIN project p on pp.project_id = p.project_id WHERE pp.activity_id IS NULL AND p.opportunity_id = $1 AND p.created_by <> function_stamp ORDER BY pp.organization_id
  LOOP
      -- check to see if the new project has a participation record with the current participation record's organization_id
      SELECT INTO new_participation_id participation_id FROM participation pp WHERE pp.project_id = new_project_id AND pp.activity_id IS NULL AND pp.organization_id = participation_record.organization_id;
      IF new_participation_id IS NULL THEN
	-- No? create the participation record
	 EXECUTE 'INSERT INTO participation(project_id, organization_id, created_by, created_date, updated_by, updated_date) VALUES( ' 
	    || new_project_id || ', ' || participation_record.organization_id || ', ' || quote_literal(function_stamp) || ', ' || quote_literal(current_date) || ', ' || quote_literal(function_stamp) || ', ' || quote_literal(current_date) 
	    || ') RETURNING participation_id;' INTO new_participation_id;
      END IF;        
      -- does the current participation record have any taxonomies?
      FOR participation_taxonomy_record IN SELECT * FROM participation_taxonomy pt WHERE pt.participation_id = participation_record.participation_id
      LOOP
        -- Yes? Check to see if the new project with the current participation record's organization_id have the same taxonomy? 
        SELECT INTO c_id classification_id FROM participation_taxonomy pt WHERE pt.participation_id = new_participation_id AND pt.classification_id = participation_taxonomy_record.classification_id;
        IF c_id IS NULL THEN
          -- No? create the participation_taxonomy record
	  EXECUTE 'INSERT INTO participation_taxonomy(participation_id, classification_id, field) VALUES( ' || new_participation_id || ', ' || participation_taxonomy_record.classification_id || ', ''participation_id'');';
        END IF;          
      END LOOP;        
      -- inactive/retire current participation record
      EXECUTE 'UPDATE participation SET active = false, retired_by = ' || new_participation_id || ', updated_by=' || quote_literal(function_stamp) || ', updated_date=' || quote_literal(current_date) || ' WHERE participation_id = ' || participation_record.participation_id;
  END LOOP;
  
  -- updated associated activity participation records with new project id  
  FOR  participation_record IN SELECT pp.* FROM participation pp JOIN project p ON pp.project_id = p.project_id WHERE pp.activity_id IS NOT NULL AND p.opportunity_id = $1 AND p.created_by <> function_stamp
  LOOP
    -- update associated activity participation records with new project id
    EXECUTE 'UPDATE participation SET project_id = ' || new_project_id || ', updated_by=' || quote_literal(function_stamp) || ', updated_date=' || quote_literal(current_date) || ' WHERE participation_id = ' || participation_record.participation_id;
  END LOOP;

  -- gather all project taxonomy
  FOR project_taxonomy_record IN SELECT pt.* FROM project_taxonomy pt JOIN project p ON pt.project_id = p.project_id WHERE p.opportunity_id = $1 AND p.created_by <> function_stamp
  LOOP
    -- Check to see if the new project has the current taxonomy? 
    SELECT INTO c_id classification_id FROM project_taxonomy pt WHERE pt.project_id = new_project_id AND pt.classification_id = project_taxonomy_record.classification_id;
    IF c_id IS NULL THEN
      -- No? create the project_taxonomy record
      EXECUTE 'INSERT INTO project_taxonomy(project_id, classification_id, field) VALUES( ' || new_project_id || ', ' || project_taxonomy_record.classification_id || ', ''project_id'');';
    END IF;
  END LOOP; 

  RETURN TRUE;
  
EXCEPTION WHEN others THEN
    RETURN FALSE;
END;
