-- create a temporary org splitting function to support logic
CREATE OR REPLACE FUNCTION pmt_split_orgs_temp(org_id integer, list character varying) RETURNS BOOLEAN AS $$
DECLARE 
  function_stamp text;
  org_list text[];
  org text;
  pp_ids integer[];
  pp_id integer;
  new_pp_id integer;
  org_id integer;
BEGIN
  org_list := string_to_array($2, ',');

  SELECT INTO pp_ids array_agg(participation_id)::INT[] FROM participation WHERE organization_id = $1; --"{3865,3866,3867,3875,3876,3877}"
  
  function_stamp := 'BMGF Data Scrub';	

  FOREACH org IN ARRAY org_list LOOP
    -- retire current record
    -- get the org_if (if exsisting/create if not)
    SELECT INTO org_id organization_id FROM organization WHERE lower(name) = lower(org);
    IF org_id IS NULL THEN
       EXECUTE 'INSERT INTO organization(name, address1, address2, city, state_providence, postal_code, country, direct_phone, mobile_phone, fax, email, url, active, created_by, updated_by) ' || 
	'(SELECT '  || quote_literal(org) || ', address1, address2, city, state_providence, postal_code, country, direct_phone, mobile_phone, fax, email, url, active, ' || quote_literal(function_stamp) || ', ' || quote_literal(function_stamp) || 
	' FROM organization WHERE organization_id = ' || $1 || ') RETURNING organization_id;' INTO org_id;
    END IF;
    IF pp_ids IS NOT NULL THEN
    FOREACH pp_id IN ARRAY pp_ids LOOP
      EXECUTE 'INSERT INTO participation(project_id, activity_id, organization_id, active, retired_by, created_by, updated_by) ' ||
	'(SELECT project_id, activity_id, ' || org_id || ', active, retired_by, ' || quote_literal(function_stamp) || ', ' || quote_literal(function_stamp) || 
	' FROM participation WHERE participation_id = '|| pp_id ||') RETURNING participation_id;' INTO new_pp_id;
      EXECUTE 'INSERT INTO participation_taxonomy(participation_id, classification_id, field) ' ||
        '(SELECT ' || new_pp_id || ', classification_id, field FROM participation_taxonomy WHERE participation_id = '|| pp_id ||');';
    END LOOP;
    END IF;
  END LOOP;

  --retire
  EXECUTE 'UPDATE organization SET retired_by = ' || org_id || ', active = false, updated_by = ' || quote_literal(function_stamp) || ', updated_date = current_date WHERE organization_id = '|| $1 ||';';
  EXECUTE 'UPDATE participation SET retired_by = ' || org_id || ', active = false, updated_by = ' || quote_literal(function_stamp) || ', updated_date = current_date  WHERE organization_id = '|| $1 ||';';
  EXECUTE 'UPDATE contact SET organization_id = ' || org_id || ' WHERE organization_id = '|| $1 ||';';
  
  RETURN TRUE;
  
-- EXCEPTION WHEN others THEN
--     RETURN FALSE;
END;
$$ LANGUAGE plpgsql VOLATILE;

