-- new parent_activity_id field for linking activities
ALTER TABLE activity ADD COLUMN parent_activity_id integer;

-- new data_group_id field (removing data group from taxonomies)
ALTER TABLE activity ADD COLUMN data_group_id integer;

-- add temporary field to track moved projects
ALTER TABLE activity ADD COLUMN was_project boolean;

-- new fields for activity to hold data from project (these are BMGF specific fields)
ALTER TABLE activity ADD COLUMN impact character varying;
ALTER TABLE activity ADD COLUMN people_affected integer;
ALTER TABLE activity ADD COLUMN fte integer;

/*************************************************************************
  1. Move all project data over
*************************************************************************/
-- move all project data to activity
INSERT INTO activity (project_id, title, label, description, start_date, end_date, tags, active, created_by, created_date, 
       updated_by, opportunity_id, url, impact, people_affected, fte, was_project)  
(SELECT project_id, title, label, description, start_date, end_date, tags, active, created_by, created_date, 
	'cs_2.0.10.0', opportunity_id, url, impact, people_affected, fte, true  FROM project);
	
-- remove "fake" projects (projects that were created only b/c a project was required for an activity)
DELETE FROM activity WHERE was_project = true AND project_id IN (774,773,775,2);

-- update the data_group_id field on activity with the classification_id for the data group
UPDATE activity SET data_group_id = subset.classification_id
FROM (
	SELECT a.activity_id, c.classification_id, c.name
	FROM activity a
	LEFT JOIN project_taxonomy pt
	ON a.project_id = pt.project_id
	JOIN classification c
	ON pt.classification_id = c.classification_id
	WHERE pt.classification_id IN (SELECT classification_id FROM classification WHERE taxonomy_id = (SELECT taxonomy_id FROM taxonomy WHERE name = 'Data Group'))
) as subset
WHERE activity.activity_id = subset.activity_id;

-- create a relationship to parent activities (activities that were projects)
UPDATE activity SET parent_activity_id = null;
UPDATE activity SET parent_activity_id = subset.activity_id
FROM (
	SELECT project_id, activity_id, title
	FROM activity
	WHERE was_project = true
) as subset
WHERE activity.project_id = subset.project_id AND was_project IS NULL;

-- move over all taxonomies (except data group)
INSERT INTO activity_taxonomy (activity_id, classification_id, field) 
(
	SELECT a.activity_id, pt.classification_id, 'activity_id'
	FROM project_taxonomy pt
	JOIN activity a	
	ON pt.project_id = a.project_id
	WHERE pt.classification_id NOT IN (SELECT classification_id FROM classification WHERE taxonomy_id = (SELECT taxonomy_id FROM taxonomy WHERE name = 'Data Group'))
);

-- move over financial data
UPDATE financial SET activity_id = subset.activity_id
FROM (
SELECT f.financial_id, a.activity_id
FROM financial f
JOIN activity a
ON f.project_id = a.project_id
WHERE f.activity_id IS NULL AND a.was_project = true
) as subset
WHERE financial.financial_id = subset.financial_id;

-- move over detail data
UPDATE detail SET activity_id = subset.activity_id
FROM (
SELECT d.detail_id, a.activity_id
FROM detail d
JOIN activity a
ON d.project_id = a.project_id
WHERE d.activity_id IS NULL AND a.was_project = true
) as subset
WHERE detail.detail_id = subset.detail_id;

-- move over participation data
UPDATE participation SET activity_id = subset.activity_id
FROM (
SELECT p.participation_id, a.activity_id
FROM participation p
JOIN activity a
ON p.project_id = a.project_id
WHERE p.activity_id IS NULL AND a.was_project = true
) as subset
WHERE participation.participation_id = subset.participation_id;

-- move over project_contacts
INSERT INTO activity_contact (activity_id, contact_id)
(	SELECT a.activity_id, pc.contact_id
	FROM project_contact pc
	JOIN activity a
	ON pc.project_id = a.project_id 
	WHERE a.was_project = true);

